  FileText, 
  Download, 
  Calendar, 
  Filter, 
  TrendingUp,
  AlertTriangle,
  CheckCircle2,
  Users,
  Award,
  Clock,
  Search
} from "lucide-react";

export default function ComplianceReports() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [selectedReport, setSelectedReport] = useState("overview");
