import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Star, MessageSquare, Plus, Users } from "lucide-react";

interface FeedbackFormData {
  sessionId: number;
  enrollmentId: number;
  overallRating: number;
  contentRating: number;
  trainerRating: number;
  relevanceRating: number;
  comments: string;
  suggestions: string;
}

export default function TrainingFeedback() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedSession, setSelectedSession] = useState<any>(null);
  const [feedbackForm, setFeedbackForm] = useState<FeedbackFormData>({
    sessionId: 0,
    enrollmentId: 0,
    overallRating: 0,
    contentRating: 0,
    trainerRating: 0,
    relevanceRating: 0,
    comments: "",
    suggestions: "",
  });

  // Get completed training sessions for the current user
  const { data: completedSessions = [], isLoading } = useQuery({
    queryKey: ["/api/training-enrollments/completed", user?.id],
    retry: false,
  });

  // Get existing feedback
  const { data: existingFeedback = [] } = useQuery({
    queryKey: ["/api/training-feedback", user?.id],
    retry: false,
  });

  const submitFeedbackMutation = useMutation({
    mutationFn: async (feedbackData: FeedbackFormData) => {
      const response = await apiRequest("POST", "/api/training-feedback", feedbackData);
      if (!response.ok) {
        throw new Error("Failed to submit feedback");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/training-feedback"] });
      setIsModalOpen(false);
      resetForm();
      toast({
        title: "Feedback Submitted",
        description: "Thank you for your feedback!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit feedback",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFeedbackForm({
      sessionId: 0,
      enrollmentId: 0,
      overallRating: 0,
      contentRating: 0,
      trainerRating: 0,
      relevanceRating: 0,
      comments: "",
      suggestions: "",
    });
    setSelectedSession(null);
  };

  const handleSubmitFeedback = () => {
    if (!selectedSession || !feedbackForm.overallRating || !feedbackForm.contentRating || 
        !feedbackForm.trainerRating || !feedbackForm.relevanceRating) {
      toast({
        title: "Error",
        description: "Please fill in all required ratings",
        variant: "destructive",
      });
      return;
    }

    submitFeedbackMutation.mutate({
      ...feedbackForm,
      sessionId: selectedSession.sessionId,
      enrollmentId: selectedSession.id,
    });
  };

  const openFeedbackModal = (session: any) => {
    setSelectedSession(session);
    setFeedbackForm({
      ...feedbackForm,
      sessionId: session.sessionId,
      enrollmentId: session.id,
    });
    setIsModalOpen(true);
  };

  const StarRating = ({ value, onChange, label }: { value: number; onChange: (rating: number) => void; label: string }) => (
    <div className="space-y-2">
      <Label>{label} *</Label>
      <div className="flex space-x-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            onClick={() => onChange(star)}
            className={`p-1 rounded ${value >= star ? 'text-yellow-500' : 'text-gray-300'} hover:text-yellow-400`}
          >
            <Star className="h-5 w-5 fill-current" />
          </button>
        ))}
      </div>
    </div>
  );

  if (isLoading) {
    return <div className="animate-pulse text-lg">Loading...</div>;
  }

  // Filter sessions that don't have feedback yet
  const sessionsNeedingFeedback = (completedSessions as any[]).filter((session: any) => 
    !(existingFeedback as any[]).some((feedback: any) => feedback.enrollmentId === session.id)
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Training Feedback</h2>
      </div>

      {/* Sessions needing feedback */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <MessageSquare className="h-5 w-5 mr-2" />
            Sessions Awaiting Feedback
          </CardTitle>
        </CardHeader>
        <CardContent>
          {sessionsNeedingFeedback.length === 0 ? (
            <p className="text-gray-500">No completed sessions awaiting feedback.</p>
          ) : (
            <div className="space-y-4">
              {sessionsNeedingFeedback.map((session: any) => (
                <div key={session.id} className="border rounded-lg p-4 flex justify-between items-center">
                  <div>
                    <h3 className="font-semibold">{session.sessionTitle || session.title}</h3>
                    <p className="text-sm text-gray-600">
                      Completed on: {new Date(session.completionDate).toLocaleDateString()}
                    </p>
                    <Badge variant="outline" className="mt-1">
                      {session.sessionDate ? new Date(session.sessionDate).toLocaleDateString() : 'N/A'}
                    </Badge>
                  </div>
                  <Button onClick={() => openFeedbackModal(session)}>
                    <Plus className="h-4 w-4 mr-2" />
                    Give Feedback
                  </Button>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Submitted feedback history */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Users className="h-5 w-5 mr-2" />
            Feedback History
          </CardTitle>
        </CardHeader>
        <CardContent>
          {(existingFeedback as any[]).length === 0 ? (
            <p className="text-gray-500">No feedback submitted yet.</p>
          ) : (
            <div className="space-y-4">
              {(existingFeedback as any[]).map((feedback: any) => (
                <div key={feedback.id} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-semibold">{feedback.sessionTitle}</h3>
                      <p className="text-sm text-gray-600">
                        Submitted: {new Date(feedback.submittedAt).toLocaleDateString()}
                      </p>
                    </div>
                    <div className="text-right">
                      <div className="flex items-center">
                        <Star className="h-4 w-4 text-yellow-500 fill-current mr-1" />
                        <span>{feedback.overallRating}/5</span>
                      </div>
                    </div>
                  </div>
                  {feedback.comments && (
                    <p className="mt-2 text-sm">{feedback.comments}</p>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Feedback Modal */}
      <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Training Feedback</DialogTitle>
          </DialogHeader>

          {selectedSession && (
            <div className="space-y-6">
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="font-semibold">{selectedSession.sessionTitle || selectedSession.title}</h3>
                <p className="text-sm text-gray-600">
                  Session Date: {selectedSession.sessionDate ? new Date(selectedSession.sessionDate).toLocaleDateString() : 'N/A'}
                </p>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <StarRating
                  value={feedbackForm.overallRating}
                  onChange={(rating) => setFeedbackForm({ ...feedbackForm, overallRating: rating })}
                  label="Overall Rating"
                />
                <StarRating
                  value={feedbackForm.contentRating}
                  onChange={(rating) => setFeedbackForm({ ...feedbackForm, contentRating: rating })}
                  label="Content Quality"
                />
                <StarRating
                  value={feedbackForm.trainerRating}
                  onChange={(rating) => setFeedbackForm({ ...feedbackForm, trainerRating: rating })}
                  label="Trainer Effectiveness"
                />
                <StarRating
                  value={feedbackForm.relevanceRating}
                  onChange={(rating) => setFeedbackForm({ ...feedbackForm, relevanceRating: rating })}
                  label="Relevance to Job"
                />
              </div>

              <div className="space-y-4">
                <div>
                  <Label htmlFor="comments">Comments</Label>
                  <Textarea
                    id="comments"
                    placeholder="Share your thoughts about the training..."
                    value={feedbackForm.comments}
                    onChange={(e) => setFeedbackForm({ ...feedbackForm, comments: e.target.value })}
                  />
                </div>
                <div>
                  <Label htmlFor="suggestions">Suggestions for Improvement</Label>
                  <Textarea
                    id="suggestions"
                    placeholder="How could this training be improved?"
                    value={feedbackForm.suggestions}
                    onChange={(e) => setFeedbackForm({ ...feedbackForm, suggestions: e.target.value })}
                  />
                </div>
              </div>

              <div className="flex justify-end space-x-3">
                <Button variant="outline" onClick={() => setIsModalOpen(false)}>
                  Cancel
                </Button>
                <Button 
                  onClick={handleSubmitFeedback}
                  disabled={submitFeedbackMutation.isPending}
                >
                  {submitFeedbackMutation.isPending ? "Submitting..." : "Submit Feedback"}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/compliance-managem.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Shield, Plus, Edit, Trash2, Search, Filter, AlertTriangle } from "lucide-react";

interface ComplianceRequirement {
  id: number;
  standard: string;
  requirement: string;
  description: string;
  frequency: string;
  department: string;
  role: string;
  trainingCatalogId: number | null;
  isActive: boolean;
  createdAt: string;
}

interface RequirementFormData {
  standard: string;
  requirement: string;
  description: string;
  frequency: string;
  department: string;
  role: string;
  trainingCatalogId: string;
  isActive: boolean;
}

export default function ComplianceManagement() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingRequirement, setEditingRequirement] = useState<ComplianceRequirement | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStandard, setFilterStandard] = useState("");
  const [filterDepartment, setFilterDepartment] = useState("");
  const [requirementForm, setRequirementForm] = useState<RequirementFormData>({
    standard: "",
    requirement: "",
    description: "",
    frequency: "",
    department: "",
    role: "",
    trainingCatalogId: "",
    isActive: true,
  });

  // Get compliance requirements
  const { data: complianceRequirements = [], isLoading } = useQuery({
    queryKey: ["/api/compliance-requirements"],
    retry: false,
    enabled: user?.role === 'hr_admin' || user?.role === 'manager',
  });

  // Get training catalog for linking
  const { data: trainingCatalog = [] } = useQuery({
    queryKey: ["/api/training-catalog"],
    retry: false,
    enabled: user?.role === 'hr_admin' || user?.role === 'manager',
  });

  const createRequirementMutation = useMutation({
    mutationFn: async (requirementData: RequirementFormData) => {
      const response = await apiRequest("POST", "/api/compliance-requirements", {
        ...requirementData,
        trainingCatalogId: requirementData.trainingCatalogId ? parseInt(requirementData.trainingCatalogId) : null,
      });
      if (!response.ok) {
        throw new Error("Failed to create requirement");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/compliance-requirements"] });
      setIsModalOpen(false);
      resetForm();
      toast({
        title: "Requirement Created",
        description: "Compliance requirement has been created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create compliance requirement",
        variant: "destructive",
      });
    },
  });

  const updateRequirementMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: RequirementFormData }) => {
      const response = await apiRequest("PUT", `/api/compliance-requirements/${id}`, {
        ...data,
        trainingCatalogId: data.trainingCatalogId ? parseInt(data.trainingCatalogId) : null,
      });
      if (!response.ok) {
        throw new Error("Failed to update requirement");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/compliance-requirements"] });
      setIsModalOpen(false);
      resetForm();
      toast({
        title: "Requirement Updated",
        description: "Compliance requirement has been updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update compliance requirement",
        variant: "destructive",
      });
    },
  });

  const deleteRequirementMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest("DELETE", `/api/compliance-requirements/${id}`, {});
      if (!response.ok) {
        throw new Error("Failed to delete requirement");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/compliance-requirements"] });
      toast({
        title: "Requirement Deleted",
        description: "Compliance requirement has been removed",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete compliance requirement",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setRequirementForm({
      standard: "",
      requirement: "",
      description: "",
      frequency: "",
      department: "",
      role: "",
      trainingCatalogId: "",
      isActive: true,
    });
    setEditingRequirement(null);
  };

  const openCreateModal = () => {
    resetForm();
    setIsModalOpen(true);
  };

  const openEditModal = (requirement: ComplianceRequirement) => {
    setEditingRequirement(requirement);
    setRequirementForm({
      standard: requirement.standard,
      requirement: requirement.requirement,
      description: requirement.description || "",
      frequency: requirement.frequency || "",
      department: requirement.department || "",
      role: requirement.role || "",
      trainingCatalogId: requirement.trainingCatalogId?.toString() || "",
      isActive: requirement.isActive,
    });
    setIsModalOpen(true);
  };

  const handleSubmit = () => {
    if (!requirementForm.standard || !requirementForm.requirement) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    if (editingRequirement) {
      updateRequirementMutation.mutate({ id: editingRequirement.id, data: requirementForm });
    } else {
      createRequirementMutation.mutate(requirementForm);
    }
  };

  const handleDelete = (requirement: ComplianceRequirement) => {
    if (confirm(`Are you sure you want to delete the requirement "${requirement.requirement}"?`)) {
      deleteRequirementMutation.mutate(requirement.id);
    }
  };

  if (isLoading) {
    return <div className="animate-pulse text-lg">Loading...</div>;
  }

  if (user?.role !== 'hr_admin' && user?.role !== 'manager') {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">Access denied. Only HR admins and managers can view this page.</p>
      </div>
    );
  }

  // Filter requirements
  const filteredRequirements = (complianceRequirements as ComplianceRequirement[]).filter((req) => {
    const matchesSearch = req.requirement.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         req.standard.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         req.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStandard = !filterStandard || req.standard === filterStandard;
    const matchesDepartment = !filterDepartment || req.department === filterDepartment;
    return matchesSearch && matchesStandard && matchesDepartment;
  });

  // Get unique standards and departments for filtering
  const uniqueStandards = Array.from(new Set((complianceRequirements as ComplianceRequirement[]).map(req => req.standard)));
  const uniqueDepartments = Array.from(new Set((complianceRequirements as ComplianceRequirement[]).map(req => req.department).filter(Boolean)));

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Compliance Requirements Management</h2>
        {user?.role === 'hr_admin' && (
          <Button onClick={openCreateModal}>
            <Plus className="h-4 w-4 mr-2" />
            Add Requirement
          </Button>
        )}
      </div>

      {/* Search and filters */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex flex-wrap gap-4">
            <div className="flex-1 min-w-[200px]">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search requirements..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Select value={filterStandard} onValueChange={setFilterStandard}>
              <SelectTrigger className="w-[200px]">
                <SelectValue placeholder="Filter by Standard" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Standards</SelectItem>
                {uniqueStandards.map((standard) => (
                  <SelectItem key={standard} value={standard}>
                    {standard}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Select value={filterDepartment} onValueChange={setFilterDepartment}>
              <SelectTrigger className="w-[200px]">
                <SelectValue placeholder="Filter by Department" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Departments</SelectItem>
                {uniqueDepartments.map((department) => (
                  <SelectItem key={department} value={department}>
                    {department}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Requirements list */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Shield className="h-5 w-5 mr-2" />
            Compliance Requirements ({filteredRequirements.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          {filteredRequirements.length === 0 ? (
            <p className="text-gray-500">No compliance requirements found.</p>
          ) : (
            <div className="space-y-4">
              {filteredRequirements.map((requirement) => (
                <div key={requirement.id} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="font-semibold">{requirement.requirement}</h3>
                        {!requirement.isActive && (
                          <Badge variant="secondary">Inactive</Badge>
                        )}
                      </div>
                      <p className="text-sm text-gray-600 mb-2">{requirement.description}</p>
                      <div className="flex flex-wrap gap-2">
                        <Badge variant="outline">{requirement.standard}</Badge>
                        {requirement.frequency && (
                          <Badge variant="outline">Frequency: {requirement.frequency}</Badge>
                        )}
                        {requirement.department && (
                          <Badge variant="outline">Dept: {requirement.department}</Badge>
                        )}
                        {requirement.role && (
                          <Badge variant="outline">Role: {requirement.role}</Badge>
                        )}
                      </div>
                      {requirement.trainingCatalogId && (
                        <p className="text-xs text-blue-600 mt-1">
                          Linked to training catalog
                        </p>
                      )}
                    </div>
                    {user?.role === 'hr_admin' && (
                      <div className="flex space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => openEditModal(requirement)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDelete(requirement)}
                          className="text-red-600 hover:text-red-700"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Create/Edit Modal */}
      <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>
              {editingRequirement ? "Edit Compliance Requirement" : "Create Compliance Requirement"}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="standard">Compliance Standard *</Label>
                <Input
                  id="standard"
                  placeholder="e.g., ISO45001, OSHA, GDPR"
                  value={requirementForm.standard}
                  onChange={(e) => setRequirementForm({ ...requirementForm, standard: e.target.value })}
                />
              </div>
              <div>
                <Label htmlFor="frequency">Frequency</Label>
                <Select value={requirementForm.frequency} onValueChange={(value) => setRequirementForm({ ...requirementForm, frequency: value })}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select frequency" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="annual">Annual</SelectItem>
                    <SelectItem value="biannual">Biannual</SelectItem>
                    <SelectItem value="quarterly">Quarterly</SelectItem>
                    <SelectItem value="monthly">Monthly</SelectItem>
                    <SelectItem value="ongoing">Ongoing</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="requirement">Requirement Title *</Label>
              <Input
                id="requirement"
                placeholder="Brief title of the requirement"
                value={requirementForm.requirement}
                onChange={(e) => setRequirementForm({ ...requirementForm, requirement: e.target.value })}
              />
            </div>

            <div>
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Detailed description of the compliance requirement"
                value={requirementForm.description}
                onChange={(e) => setRequirementForm({ ...requirementForm, description: e.target.value })}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="department">Department</Label>
                <Select value={requirementForm.department} onValueChange={(value) => setRequirementForm({ ...requirementForm, department: value })}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select department" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">All Departments</SelectItem>
                    <SelectItem value="Operations">Operations</SelectItem>
                    <SelectItem value="Safety">Safety</SelectItem>
                    <SelectItem value="Quality">Quality</SelectItem>
                    <SelectItem value="Production">Production</SelectItem>
                    <SelectItem value="HR">HR</SelectItem>
                    <SelectItem value="Finance">Finance</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="role">Role</Label>
                <Select value={requirementForm.role} onValueChange={(value) => setRequirementForm({ ...requirementForm, role: value })}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select role" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">All Roles</SelectItem>
                    <SelectItem value="employee">Employee</SelectItem>
                    <SelectItem value="manager">Manager</SelectItem>
                    <SelectItem value="hr_admin">HR Admin</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="trainingCatalogId">Linked Training</Label>
              <Select value={requirementForm.trainingCatalogId} onValueChange={(value) => setRequirementForm({ ...requirementForm, trainingCatalogId: value })}>
                <SelectTrigger>
                  <SelectValue placeholder="Link to training course (optional)" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">No linked training</SelectItem>
                  {(trainingCatalog as any[]).map((training) => (
                    <SelectItem key={training.id} value={training.id.toString()}>
                      {training.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="flex items-center space-x-2">
              <input
                type="checkbox"
                id="isActive"
                checked={requirementForm.isActive}
                onChange={(e) => setRequirementForm({ ...requirementForm, isActive: e.target.checked })}
              />
              <Label htmlFor="isActive">Active Requirement</Label>
            </div>
          </div>

          <div className="flex justify-end space-x-3 mt-6">
            <Button variant="outline" onClick={() => setIsModalOpen(false)}>
              Cancel
            </Button>
            <Button 
              onClick={handleSubmit}
              disabled={createRequirementMutation.isPending || updateRequirementMutation.isPending}
            >
              {createRequirementMutation.isPending || updateRequirementMutation.isPending 
                ? "Saving..." 
                : editingRequirement ? "Update Requirement" : "Create Requirement"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/evidence-attachmen.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { FileText, Upload, Download, Eye, Plus, Trash2, AlertCircle } from "lucide-react";

interface AttachmentFormData {
  enrollmentId: number;
  sessionId: number;
  description: string;
  file: File | null;
}

export default function EvidenceAttachments() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEnrollment, setSelectedEnrollment] = useState<any>(null);
  const [attachmentForm, setAttachmentForm] = useState<AttachmentFormData>({
    enrollmentId: 0,
    sessionId: 0,
    description: "",
    file: null,
  });

  // Get user's completed training sessions
  const { data: userEnrollments = [], isLoading } = useQuery({
    queryKey: ["/api/training-enrollments/user", user?.id],
    retry: false,
  });

  // Get existing attachments
  const { data: existingAttachments = [] } = useQuery({
    queryKey: ["/api/evidence-attachments", user?.id],
    retry: false,
  });

  const uploadAttachmentMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const response = await fetch('/api/evidence-attachments', {
        method: 'POST',
        headers: {
          'Authorization': localStorage.getItem('authToken') || '',
        },
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Upload failed: ${errorText}`);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/evidence-attachments"] });
      setIsModalOpen(false);
      resetForm();
      toast({
        title: "Evidence Uploaded",
        description: "Training evidence has been uploaded successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Upload Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteAttachmentMutation = useMutation({
    mutationFn: async (attachmentId: number) => {
      const response = await apiRequest("DELETE", `/api/evidence-attachments/${attachmentId}`, {});
      if (!response.ok) {
        throw new Error("Failed to delete attachment");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/evidence-attachments"] });
      toast({
        title: "Evidence Deleted",
        description: "Training evidence has been removed",
      });
    },
    onError: () => {
      toast({
        title: "Delete Failed",
        description: "Failed to delete attachment",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setAttachmentForm({
      enrollmentId: 0,
      sessionId: 0,
      description: "",
      file: null,
    });
    setSelectedEnrollment(null);
  };

  const handleSubmitAttachment = () => {
    if (!selectedEnrollment || !attachmentForm.file) {
      toast({
        title: "Error",
        description: "Please select a training session and upload a file",
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append('evidence', attachmentForm.file);
    formData.append('enrollmentId', selectedEnrollment.id.toString());
    formData.append('sessionId', selectedEnrollment.sessionId.toString());
    formData.append('description', attachmentForm.description);

    uploadAttachmentMutation.mutate(formData);
  };

  const openUploadModal = (enrollment: any) => {
    setSelectedEnrollment(enrollment);
    setAttachmentForm({
      ...attachmentForm,
      enrollmentId: enrollment.id,
      sessionId: enrollment.sessionId,
    });
    setIsModalOpen(true);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // Check file size (max 10MB)
    if (file.size > 10 * 1024 * 1024) {
      toast({
        title: "File Too Large",
        description: "File size must be less than 10MB",
        variant: "destructive",
      });
      e.target.value = '';
      return;
    }

    setAttachmentForm({ ...attachmentForm, file });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const downloadAttachment = (attachment: any) => {
    // Create a download link
    const link = document.createElement('a');
    link.href = `/api/evidence-attachments/download/${attachment.id}`;
    link.download = attachment.originalFileName;
    link.style.display = 'none';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  if (isLoading) {
    return <div className="animate-pulse text-lg">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Training Evidence Attachments</h2>
      </div>

      {/* Training sessions available for evidence upload */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Upload className="h-5 w-5 mr-2" />
            Upload Training Evidence
          </CardTitle>
        </CardHeader>
        <CardContent>
          {(userEnrollments as any[]).length === 0 ? (
            <p className="text-gray-500">No training sessions available for evidence upload.</p>
          ) : (
            <div className="space-y-4">
              {(userEnrollments as any[]).map((enrollment: any) => (
                <div key={enrollment.id} className="border rounded-lg p-4 flex justify-between items-center">
                  <div>
                    <h3 className="font-semibold">{enrollment.sessionTitle || enrollment.title}</h3>
                    <p className="text-sm text-gray-600">
                      Status: <Badge variant={enrollment.status === 'completed' ? 'default' : 'secondary'}>
                        {enrollment.status}
                      </Badge>
                    </p>
                    <p className="text-sm text-gray-600">
                      Session Date: {enrollment.sessionDate ? new Date(enrollment.sessionDate).toLocaleDateString() : 'N/A'}
                    </p>
                    {enrollment.completionDate && (
                      <p className="text-sm text-gray-600">
                        Completed: {new Date(enrollment.completionDate).toLocaleDateString()}
                      </p>
                    )}
                  </div>
                  <Button 
                    onClick={() => openUploadModal(enrollment)}
                    disabled={enrollment.status !== 'completed' && enrollment.status !== 'attended'}
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Add Evidence
                  </Button>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Existing attachments */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <FileText className="h-5 w-5 mr-2" />
            Uploaded Evidence
          </CardTitle>
        </CardHeader>
        <CardContent>
          {(existingAttachments as any[]).length === 0 ? (
            <p className="text-gray-500">No evidence files uploaded yet.</p>
          ) : (
            <div className="space-y-4">
              {(existingAttachments as any[]).map((attachment: any) => (
                <div key={attachment.id} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h3 className="font-semibold">{attachment.originalFileName}</h3>
                      <p className="text-sm text-gray-600">
                        Training: {attachment.sessionTitle}
                      </p>
                      <p className="text-sm text-gray-600">
                        Size: {formatFileSize(attachment.fileSize)}
                      </p>
                      <p className="text-sm text-gray-600">
                        Uploaded: {new Date(attachment.uploadedAt).toLocaleDateString()}
                      </p>
                      {attachment.description && (
                        <p className="text-sm mt-2">{attachment.description}</p>
                      )}
                    </div>
                    <div className="flex space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => downloadAttachment(attachment)}
                      >
                        <Download className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          if (confirm('Are you sure you want to delete this evidence file?')) {
                            deleteAttachmentMutation.mutate(attachment.id);
                          }
                        }}
                        className="text-red-600 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Upload Modal */}
      <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Upload Training Evidence</DialogTitle>
          </DialogHeader>

          {selectedEnrollment && (
            <div className="space-y-6">
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="font-semibold">{selectedEnrollment.sessionTitle || selectedEnrollment.title}</h3>
                <p className="text-sm text-gray-600">
                  Session Date: {selectedEnrollment.sessionDate ? new Date(selectedEnrollment.sessionDate).toLocaleDateString() : 'N/A'}
                </p>
                <p className="text-sm text-gray-600">
                  Status: {selectedEnrollment.status}
                </p>
              </div>

              <div className="space-y-4">
                <div>
                  <Label htmlFor="evidence-file">Evidence File *</Label>
                  <Input
                    id="evidence-file"
                    type="file"
                    onChange={handleFileChange}
                    accept=".pdf,.doc,.docx,.jpg,.jpeg,.png,.xlsx,.xls,.ppt,.pptx"
                    className="mt-1"
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Supported formats: PDF, DOC, DOCX, JPG, PNG, XLSX, XLS, PPT, PPTX (Max 10MB)
                  </p>
                  {attachmentForm.file && (
                    <p className="text-sm text-green-600 mt-1">
                      Selected: {attachmentForm.file.name} ({formatFileSize(attachmentForm.file.size)})
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    placeholder="Describe this evidence document..."
                    value={attachmentForm.description}
                    onChange={(e) => setAttachmentForm({ ...attachmentForm, description: e.target.value })}
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2 text-sm text-amber-600">
                <AlertCircle className="h-4 w-4" />
                <span>Evidence files will be stored securely and may be reviewed by your manager or HR.</span>
              </div>

              <div className="flex justify-end space-x-3">
                <Button variant="outline" onClick={() => setIsModalOpen(false)}>
                  Cancel
                </Button>
                <Button 
                  onClick={handleSubmitAttachment}
                  disabled={uploadAttachmentMutation.isPending || !attachmentForm.file}
                >
                  {uploadAttachmentMutation.isPending ? "Uploading..." : "Upload Evidence"}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/manager-evaluation.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Star, Users, TrendingUp, Plus, Calendar, CheckCircle } from "lucide-react";

interface EvaluationFormData {
  enrollmentId: number;
  employeeId: string;
  evaluationDate: string;
  knowledgeApplication: number;
  behaviorChange: number;
  performanceImprovement: number;
  complianceAdherence: number;
  overallEffectiveness: number;
  comments: string;
  actionPlan: string;
  followUpRequired: boolean;
  followUpDate: string;
}

export default function ManagerEvaluations() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEnrollment, setSelectedEnrollment] = useState<any>(null);
  const [evaluationForm, setEvaluationForm] = useState<EvaluationFormData>({
    enrollmentId: 0,
    employeeId: "",
    evaluationDate: "",
    knowledgeApplication: 0,
    behaviorChange: 0,
    performanceImprovement: 0,
    complianceAdherence: 0,
    overallEffectiveness: 0,
    comments: "",
    actionPlan: "",
    followUpRequired: false,
    followUpDate: "",
  });

  // Get employees in manager's department who have completed training
  const { data: employeeEnrollments = [], isLoading } = useQuery({
    queryKey: ["/api/manager-evaluations/pending", user?.id],
    retry: false,
    enabled: user?.role === 'manager' || user?.role === 'hr_admin',
  });

  // Get existing evaluations
  const { data: existingEvaluations = [] } = useQuery({
    queryKey: ["/api/effectiveness-evaluations", user?.id],
    retry: false,
    enabled: user?.role === 'manager' || user?.role === 'hr_admin',
  });

  const submitEvaluationMutation = useMutation({
    mutationFn: async (evaluationData: EvaluationFormData) => {
      const response = await apiRequest("POST", "/api/effectiveness-evaluations", evaluationData);
      if (!response.ok) {
        throw new Error("Failed to submit evaluation");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/effectiveness-evaluations"] });
      queryClient.invalidateQueries({ queryKey: ["/api/manager-evaluations/pending"] });
      setIsModalOpen(false);
      resetForm();
      toast({
        title: "Evaluation Submitted",
        description: "Training effectiveness evaluation has been recorded",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit evaluation",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setEvaluationForm({
      enrollmentId: 0,
      employeeId: "",
      evaluationDate: "",
      knowledgeApplication: 0,
      behaviorChange: 0,
      performanceImprovement: 0,
      complianceAdherence: 0,
      overallEffectiveness: 0,
      comments: "",
      actionPlan: "",
      followUpRequired: false,
      followUpDate: "",
    });
    setSelectedEnrollment(null);
  };

  const handleSubmitEvaluation = () => {
    if (!selectedEnrollment || !evaluationForm.evaluationDate || !evaluationForm.overallEffectiveness) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    if (evaluationForm.followUpRequired && !evaluationForm.followUpDate) {
      toast({
        title: "Error",
        description: "Follow-up date is required when follow-up is needed",
        variant: "destructive",
      });
      return;
    }

    submitEvaluationMutation.mutate({
      ...evaluationForm,
      enrollmentId: selectedEnrollment.id,
      employeeId: selectedEnrollment.employeeId,
    });
  };

  const openEvaluationModal = (enrollment: any) => {
    setSelectedEnrollment(enrollment);
    setEvaluationForm({
      ...evaluationForm,
      enrollmentId: enrollment.id,
      employeeId: enrollment.employeeId,
      evaluationDate: new Date().toISOString().split('T')[0],
    });
    setIsModalOpen(true);
  };

  const StarRating = ({ value, onChange, label }: { value: number; onChange: (rating: number) => void; label: string }) => (
    <div className="space-y-2">
      <Label>{label} *</Label>
      <div className="flex space-x-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            onClick={() => onChange(star)}
            className={`p-1 rounded ${value >= star ? 'text-yellow-500' : 'text-gray-300'} hover:text-yellow-400`}
          >
            <Star className="h-5 w-5 fill-current" />
          </button>
        ))}
      </div>
    </div>
  );

  if (isLoading) {
    return <div className="animate-pulse text-lg">Loading...</div>;
  }

  if (user?.role !== 'manager' && user?.role !== 'hr_admin') {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">Access denied. Only managers and HR admins can view this page.</p>
      </div>
    );
  }

  // Filter enrollments that don't have evaluations yet
  const enrollmentsNeedingEvaluation = (employeeEnrollments as any[]).filter((enrollment: any) => 
    !(existingEvaluations as any[]).some((evaluation: any) => evaluation.enrollmentId === enrollment.id)
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Manager Effectiveness Evaluations</h2>
      </div>

      {/* Pending evaluations */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <TrendingUp className="h-5 w-5 mr-2" />
            Training Awaiting Effectiveness Evaluation
          </CardTitle>
        </CardHeader>
        <CardContent>
          {enrollmentsNeedingEvaluation.length === 0 ? (
            <p className="text-gray-500">No completed training sessions awaiting evaluation.</p>
          ) : (
            <div className="space-y-4">
              {enrollmentsNeedingEvaluation.map((enrollment: any) => (
                <div key={enrollment.id} className="border rounded-lg p-4 flex justify-between items-center">
                  <div>
                    <h3 className="font-semibold">{enrollment.sessionTitle}</h3>
                    <p className="text-sm text-gray-600">
                      Employee: {enrollment.firstName} {enrollment.lastName} ({enrollment.employeeId})
                    </p>
                    <p className="text-sm text-gray-600">
                      Completed: {new Date(enrollment.completionDate).toLocaleDateString()}
                    </p>
                    <div className="flex gap-2 mt-2">
                      <Badge variant="outline">{enrollment.department}</Badge>
                      <Badge variant="secondary">{enrollment.category}</Badge>
                    </div>
                  </div>
                  <Button onClick={() => openEvaluationModal(enrollment)}>
                    <Plus className="h-4 w-4 mr-2" />
                    Evaluate
                  </Button>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Completed evaluations */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <CheckCircle className="h-5 w-5 mr-2" />
            Completed Evaluations
          </CardTitle>
        </CardHeader>
        <CardContent>
          {(existingEvaluations as any[]).length === 0 ? (
            <p className="text-gray-500">No evaluations completed yet.</p>
          ) : (
            <div className="space-y-4">
              {(existingEvaluations as any[]).map((evaluation: any) => (
                <div key={evaluation.id} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-semibold">{evaluation.sessionTitle}</h3>
                      <p className="text-sm text-gray-600">
                        Employee: {evaluation.firstName} {evaluation.lastName}
                      </p>
                      <p className="text-sm text-gray-600">
                        Evaluated: {new Date(evaluation.evaluationDate).toLocaleDateString()}
                      </p>
                    </div>
                    <div className="text-right">
                      <div className="flex items-center">
                        <Star className="h-4 w-4 text-yellow-500 fill-current mr-1" />
                        <span>{evaluation.overallEffectiveness}/5</span>
                      </div>
                      {evaluation.followUpRequired && (
                        <Badge variant="destructive" className="mt-1">
                          Follow-up Required
                        </Badge>
                      )}
                    </div>
                  </div>
                  {evaluation.comments && (
                    <p className="mt-2 text-sm">{evaluation.comments}</p>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Evaluation Modal */}
      <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Training Effectiveness Evaluation</DialogTitle>
          </DialogHeader>

          {selectedEnrollment && (
            <div className="space-y-6">
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="font-semibold">{selectedEnrollment.sessionTitle}</h3>
                <p className="text-sm text-gray-600">
                  Employee: {selectedEnrollment.firstName} {selectedEnrollment.lastName}
                </p>
                <p className="text-sm text-gray-600">
                  Completed: {new Date(selectedEnrollment.completionDate).toLocaleDateString()}
                </p>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="evaluationDate">Evaluation Date *</Label>
                  <Input
                    id="evaluationDate"
                    type="date"
                    value={evaluationForm.evaluationDate}
                    onChange={(e) => setEvaluationForm({ ...evaluationForm, evaluationDate: e.target.value })}
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <StarRating
                  value={evaluationForm.knowledgeApplication}
                  onChange={(rating) => setEvaluationForm({ ...evaluationForm, knowledgeApplication: rating })}
                  label="Knowledge Application"
                />
                <StarRating
                  value={evaluationForm.behaviorChange}
                  onChange={(rating) => setEvaluationForm({ ...evaluationForm, behaviorChange: rating })}
                  label="Behavior Change"
                />
                <StarRating
                  value={evaluationForm.performanceImprovement}
                  onChange={(rating) => setEvaluationForm({ ...evaluationForm, performanceImprovement: rating })}
                  label="Performance Improvement"
                />
                <StarRating
                  value={evaluationForm.complianceAdherence}
                  onChange={(rating) => setEvaluationForm({ ...evaluationForm, complianceAdherence: rating })}
                  label="Compliance Adherence"
                />
              </div>

              <StarRating
                value={evaluationForm.overallEffectiveness}
                onChange={(rating) => setEvaluationForm({ ...evaluationForm, overallEffectiveness: rating })}
                label="Overall Training Effectiveness"
              />

              <div className="space-y-4">
                <div>
                  <Label htmlFor="comments">Evaluation Comments</Label>
                  <Textarea
                    id="comments"
                    placeholder="Detailed assessment of training effectiveness..."
                    value={evaluationForm.comments}
                    onChange={(e) => setEvaluationForm({ ...evaluationForm, comments: e.target.value })}
                  />
                </div>
                <div>
                  <Label htmlFor="actionPlan">Action Plan</Label>
                  <Textarea
                    id="actionPlan"
                    placeholder="Next steps and recommendations..."
                    value={evaluationForm.actionPlan}
                    onChange={(e) => setEvaluationForm({ ...evaluationForm, actionPlan: e.target.value })}
                  />
                </div>
              </div>

              <div className="space-y-4">
                <div className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    id="followUpRequired"
                    checked={evaluationForm.followUpRequired}
                    onChange={(e) => setEvaluationForm({ 
                      ...evaluationForm, 
                      followUpRequired: e.target.checked,
                      followUpDate: e.target.checked ? evaluationForm.followUpDate : ""
                    })}
                  />
                  <Label htmlFor="followUpRequired">Follow-up Required</Label>
                </div>

                {evaluationForm.followUpRequired && (
                  <div>
                    <Label htmlFor="followUpDate">Follow-up Date *</Label>
                    <Input
                      id="followUpDate"
                      type="date"
                      value={evaluationForm.followUpDate}
                      onChange={(e) => setEvaluationForm({ ...evaluationForm, followUpDate: e.target.value })}
                    />
                  </div>
                )}
              </div>

              <div className="flex justify-end space-x-3">
                <Button variant="outline" onClick={() => setIsModalOpen(false)}>
                  Cancel
                </Button>
                <Button 
                  onClick={handleSubmitEvaluation}
                  disabled={submitEvaluationMutation.isPending}
                >
                  {submitEvaluationMutation.isPending ? "Submitting..." : "Submit Evaluation"}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

