                      <tbody className="bg-white divide-y divide-gray-200">
                        {employeeCompliance.slice(0, 10).map((employee: any, index: number) => (
                          <tr key={employee.employeeId} data-testid={`employee-compliance-${index}`}>
                            <td className="px-4 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                              {employee.employeeName}
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                              {employee.department || 'N/A'}
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap">
                              <Badge 
                                className={
                                  employee.complianceStatus === 'Compliant' 
                                    ? 'bg-compliance-green text-white' :
                                  employee.complianceStatus === 'Expiring Soon'
                                    ? 'bg-alert-orange text-white' :
                                    'bg-critical-red text-white'
                                }
                              >
                                {employee.complianceStatus}
                              </Badge>
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                              {employee.lastTraining || 'No record'}
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                              {employee.nextDue || 'N/A'}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="training">
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg font-semibold">Training Activity Report</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                    <div className="text-center">
                      <div className="text-3xl font-bold text-manufacturing-blue" data-testid="total-sessions">
                        {trainingSessions.length}
                      </div>
                      <div className="text-sm text-gray-600">Total Sessions</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-bold text-compliance-green" data-testid="completed-sessions">
                        {trainingSessions.filter((s: any) => s.status === 'completed').length}
                      </div>
                      <div className="text-sm text-gray-600">Completed Sessions</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-bold text-alert-orange" data-testid="scheduled-sessions">
                        {trainingSessions.filter((s: any) => s.status === 'scheduled').length}
                      </div>
                      <div className="text-sm text-gray-600">Scheduled Sessions</div>
                    </div>
                  </div>

                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-gray-50 border-b border-gray-200">
                        <tr>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Training Session
                          </th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Date
                          </th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Duration
                          </th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Status
                          </th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Trainer
                          </th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {trainingSessions.slice(0, 10).map((session: any, index: number) => (
                          <tr key={session.id} data-testid={`training-session-${index}`}>
                            <td className="px-4 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                              {session.title}
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                              {new Date(session.sessionDate).toLocaleDateString()}
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                              {session.duration}h
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap">
                              <Badge 
                                className={
                                  session.status === 'completed' 
                                    ? 'bg-compliance-green text-white' :
                                  session.status === 'scheduled'
                                    ? 'bg-manufacturing-blue text-white' :
                                    'bg-gray-500 text-white'
                                }
                              >
                                {session.status}
                              </Badge>
                            </td>
                            <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                              {session.trainerName || 'TBD'}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {canViewAuditReports && (
              <TabsContent value="audit">
                <Card>
                  <CardHeader>
                    <CardTitle className="text-lg font-semibold">Audit Trail</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="overflow-x-auto">
                      <table className="w-full">
                        <thead className="bg-gray-50 border-b border-gray-200">
                          <tr>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Timestamp
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              User
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Action
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Entity
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Changes
                            </th>
                          </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                          {auditLogs.length === 0 ? (
                            <tr>
                              <td colSpan={5} className="px-4 py-8 text-center text-gray-500" data-testid="no-audit-logs">
                                No audit logs available
                              </td>
                            </tr>
                          ) : (
                            auditLogs.slice(0, 20).map((log: any, index: number) => (
                              <tr key={log.id} data-testid={`audit-log-${index}`}>
                                <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                                  {new Date(log.performedAt).toLocaleString()}
                                </td>
                                <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-900">
                                  {log.performedBy}
                                </td>
                                <td className="px-4 py-4 whitespace-nowrap">
                                  <Badge variant="outline">
                                    {log.action}
                                  </Badge>
                                </td>
                                <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                                  {log.entityType} #{log.entityId}
                                </td>
                                <td className="px-4 py-4 text-sm text-gray-500 max-w-xs truncate">
                                  {log.changes ? JSON.stringify(log.changes) : 'N/A'}
                                </td>
                              </tr>
                            ))
                          )}
                        </tbody>
                      </table>
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>
            )}
          </Tabs>
        </div>
      </main>
    </div>
  );
}

client/src/pages/dashboard.tsx.content:
import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/sidebar";
import ComplianceMetrics from "@/components/compliance-metrics";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Clock, CheckCircle2, AlertTriangle, Users, Plus, Search, Bell } from "lucide-react";

export default function Dashboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: employeeCompliance = [], isLoading: isLoadingCompliance } = useQuery<any[]>({
    queryKey: ["/api/dashboard/employee-compliance"],
    retry: false,
  });

  const { data: trainingSessions = [] } = useQuery<any[]>({
    queryKey: ["/api/training-sessions"],
    retry: false,
  });

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  const recentActivities = trainingSessions.slice(0, 3).map((session: any) => ({
    id: session.id,
    title: session.title,
    type: session.status === 'completed' ? 'completed' : 'scheduled',
    details: `${session.status === 'completed' ? 'Completed by' : 'Scheduled for'} participants`,
    date: new Date(session.sessionDate).toLocaleDateString(),
    time: new Date(session.sessionDate).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
  }));

  const complianceAlerts = [
    {
      id: 1,
      type: 'critical',
      title: 'Critical Certification Expiring',
      description: 'Forklift operator certificates for 3 employees expire in 5 days',
      action: 'Take Action',
    },
    {
      id: 2,
      type: 'warning',
      title: 'Overdue Training',
      description: 'Emergency evacuation drill scheduled 2 weeks ago',
      action: 'Schedule Now',
    },
    {
      id: 3,
      type: 'info',
      title: 'New Regulation Update',
      description: 'ISO 45001:2024 amendment requires additional documentation',
      action: 'Learn More',
    },
  ];

  return (
    <div className="min-h-screen flex bg-gray-50">
      <Sidebar />

      <main className="flex-1 ml-64">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-20">
          <div className="px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-gray-900" data-testid="text-dashboard-title">Training Management Dashboard</h2>
                <p className="text-gray-600 mt-1">Monitor compliance status and manage training programs</p>
              </div>

              <div className="flex items-center space-x-4">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                  <input 
                    type="text" 
                    placeholder="Search trainings, employees..." 
                    className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-manufacturing-blue focus:border-transparent w-80"
                    data-testid="input-search"
                  />
                </div>

                <button className="relative p-2 text-gray-600 hover:text-gray-900 transition-colors" data-testid="button-notifications">
                  <Bell className="h-5 w-5" />
                  <span className="absolute -top-1 -right-1 w-4 h-4 bg-critical-red text-white text-xs rounded-full flex items-center justify-center">3</span>
                </button>

                <Button className="bg-manufacturing-blue hover:bg-blue-700" data-testid="button-add-training">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Training
                </Button>
              </div>
            </div>
          </div>
        </header>

        <div className="p-6 space-y-6">
          {/* Compliance Metrics */}
          <ComplianceMetrics />

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Recent Training Activities */}
            <div className="lg:col-span-2">
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg font-semibold">Recent Training Activities</CardTitle>
                    <Button variant="ghost" className="text-manufacturing-blue hover:text-blue-700" data-testid="button-view-all-activities">
                      View All
                    </Button>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {recentActivities.length === 0 ? (
                      <div className="text-center py-8 text-gray-500" data-testid="text-no-activities">
                        No recent training activities
                      </div>
                    ) : (
                      recentActivities.map((activity: any) => (
                        <div key={activity.id} className="flex items-center space-x-4 p-4 bg-gray-50 rounded-lg" data-testid={`activity-${activity.id}`}>
                          <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                            activity.type === 'completed' 
                              ? 'bg-compliance-green' 
                              : 'bg-alert-orange'
                          }`}>
                            {activity.type === 'completed' ? (
                              <CheckCircle2 className="h-5 w-5 text-white" />
                            ) : (
                              <Clock className="h-5 w-5 text-white" />
                            )}
                          </div>
                          <div className="flex-1">
                            <h4 className="font-medium text-gray-900" data-testid={`text-activity-title-${activity.id}`}>{activity.title}</h4>
                            <p className="text-sm text-gray-600" data-testid={`text-activity-details-${activity.id}`}>{activity.details}</p>
                          </div>
                          <div className="text-right">
                            <p className="text-sm font-medium text-gray-900" data-testid={`text-activity-date-${activity.id}`}>{activity.date}</p>
                            <p className="text-xs text-gray-500" data-testid={`text-activity-time-${activity.id}`}>{activity.time}</p>
                          </div>
                        </div>
                      ))
                    )}
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Compliance Alerts */}
            <div>
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg font-semibold">Compliance Alerts</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {complianceAlerts.map((alert) => (
                      <div 
                        key={alert.id} 
                        className={`p-4 border rounded-lg ${
                          alert.type === 'critical' 
                            ? 'bg-red-50 border-red-200' 
                            : alert.type === 'warning'
                            ? 'bg-orange-50 border-orange-200'
                            : 'bg-blue-50 border-blue-200'
                        }`}
                        data-testid={`alert-${alert.id}`}
                      >
                        <div className="flex items-start space-x-3">
                          <AlertTriangle className={`mt-1 h-4 w-4 ${
                            alert.type === 'critical' 
                              ? 'text-critical-red' 
                              : alert.type === 'warning'
                              ? 'text-alert-orange'
                              : 'text-manufacturing-blue'
                          }`} />
                          <div>
                            <h4 className={`font-medium ${
                              alert.type === 'critical' 
                                ? 'text-critical-red' 
                                : alert.type === 'warning'
                                ? 'text-alert-orange'
                                : 'text-manufacturing-blue'
                            }`} data-testid={`text-alert-title-${alert.id}`}>
                              {alert.title}
                            </h4>
                            <p className="text-sm text-gray-700 mt-1" data-testid={`text-alert-description-${alert.id}`}>
                              {alert.description}
                            </p>
                            <button className={`text-xs font-medium mt-2 hover:underline ${
                              alert.type === 'critical' 
                                ? 'text-critical-red' 
                                : alert.type === 'warning'
                                ? 'text-alert-orange'
                                : 'text-manufacturing-blue'
                            }`} data-testid={`button-alert-action-${alert.id}`}>
                              {alert.action}
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>

          {/* This Week's Training Schedule */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg font-semibold">This Week's Training Schedule</CardTitle>
                <div className="flex items-center space-x-2">
                  <Button variant="ghost" size="sm" className="bg-gray-100 text-gray-700 hover:bg-gray-200" data-testid="button-week-view">
                    Week
                  </Button>
                  <Button variant="ghost" size="sm" className="text-gray-600 hover:bg-gray-100" data-testid="button-month-view">
                    Month
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-7 gap-4">
                {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
                  <div key={day} className="text-xs font-medium text-gray-500 uppercase tracking-wide py-2" data-testid={`calendar-header-${day.toLowerCase()}`}>
                    {day}
                  </div>
                ))}

                {[15, 16, 17, 18, 19, 20, 21].map((date) => (
                  <div 
                    key={date} 
                    className={`p-2 min-h-24 border border-gray-100 rounded ${
                      date === 16 ? 'bg-blue-50' : 
                      date === 18 ? 'bg-green-50' :
                      date === 19 ? 'bg-orange-50' : ''
                    }`}
                    data-testid={`calendar-day-${date}`}
                  >
                    <span className="text-sm text-gray-900">{date}</span>
                    {date === 16 && (
                      <div className="mt-1 p-1 bg-manufacturing-blue text-white text-xs rounded truncate">
                        OSHA Safety
                      </div>
                    )}
                    {date === 18 && (
                      <div className="mt-1 p-1 bg-compliance-green text-white text-xs rounded truncate">
                        Quality Control
                      </div>
                    )}
                    {date === 19 && (
                      <div className="mt-1 p-1 bg-alert-orange text-white text-xs rounded truncate">
                        Fire Safety Drill
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Employee Training Status */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg font-semibold">Employee Training Status</CardTitle>
                <div className="flex items-center space-x-3">
                  <select className="border border-gray-300 rounded-md px-3 py-2 text-sm" data-testid="select-department-filter">
                    <option>All Departments</option>
                    <option>Production</option>
                    <option>Quality Control</option>
                    <option>Maintenance</option>
                  </select>
                  <Button variant="outline" className="text-manufacturing-blue border-manufacturing-blue hover:bg-blue-50" data-testid="button-export-compliance">
                    Export
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              {isLoadingCompliance ? (
                <div className="flex items-center justify-center py-8">
                  <div className="animate-pulse text-gray-500">Loading compliance data...</div>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead className="bg-gray-50 border-b border-gray-200">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Employee</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Department</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Compliance Status</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Last Training</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Next Due</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {employeeCompliance.length === 0 ? (
                        <tr>
                          <td colSpan={6} className="px-6 py-8 text-center text-gray-500" data-testid="text-no-compliance-data">
                            No employee compliance data available
                          </td>
                        </tr>
                      ) : (
                        employeeCompliance.slice(0, 10).map((employee: any, index: number) => (
                          <tr key={employee.employeeId} className="hover:bg-gray-50" data-testid={`employee-row-${index}`}>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div className="flex items-center">
                                <div className="flex-shrink-0 h-8 w-8">
                                  <div className="h-8 w-8 rounded-full bg-manufacturing-blue flex items-center justify-center">
                                    <span className="text-white text-sm font-medium">
                                      {employee.employeeName.split(' ').map((n: string) => n[0]).join('').slice(0, 2)}
                                    </span>
                                  </div>
                                </div>
                                <div className="ml-4">
                                  <div className="text-sm font-medium text-gray-900" data-testid={`text-employee-name-${index}`}>
                                    {employee.employeeName}
                                  </div>
                                  <div className="text-sm text-gray-500" data-testid={`text-employee-id-${index}`}>
                                    {employee.employeeId}
                                  </div>
                                </div>
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900" data-testid={`text-department-${index}`}>
                              {employee.department}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <Badge 
                                variant={
                                  employee.complianceStatus === 'Compliant' ? 'default' :
                                  employee.complianceStatus === 'Expiring Soon' ? 'secondary' :
                                  'destructive'
                                }
                                className={
                                  employee.complianceStatus === 'Compliant' 
                                    ? 'bg-green-100 text-compliance-green hover:bg-green-100' :
                                  employee.complianceStatus === 'Expiring Soon'
                                    ? 'bg-orange-100 text-alert-orange hover:bg-orange-100' :
                                    'bg-red-100 text-critical-red hover:bg-red-100'
                                }
                                data-testid={`badge-status-${index}`}
                              >
                                {employee.complianceStatus}
                              </Badge>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900" data-testid={`text-last-training-${index}`}>
                              {employee.lastTraining || 'No training record'}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900" data-testid={`text-next-due-${index}`}>
                              {employee.nextDue || 'N/A'}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                              <Button variant="ghost" size="sm" className="text-manufacturing-blue hover:text-blue-700 mr-3" data-testid={`button-view-employee-${index}`}>
                                View
                              </Button>
                              <Button variant="ghost" size="sm" className="text-gray-600 hover:text-gray-900" data-testid={`button-edit-employee-${index}`}>
                                Edit
                              </Button>
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/employee-records.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Search, Filter, Download, Users, Award, Calendar, FileText, X, BookOpen } from "lucide-react";

export default function EmployeeRecords() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [searchTerm, setSearchTerm] = useState("");
  const [filterDepartment, setFilterDepartment] = useState("");
  const [filterStatus, setFilterStatus] = useState("");

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: employeeCompliance = [], isLoading: isLoadingCompliance } = useQuery<any[]>({
    queryKey: ["/api/dashboard/employee-compliance"],
    retry: false,
  });

  const { data: allEmployees = [] } = useQuery({
    queryKey: ["/api/users"],
    retry: false,
    enabled: user?.role === 'hr_admin',
  });

  const { data: trainingEnrollments = [] } = useQuery({
    queryKey: ["/api/training-enrollments"],
    retry: false,
  });

  const [selectedEmployee, setSelectedEmployee] = useState<any>(null);
  const [isDetailsModalOpen, setIsDetailsModalOpen] = useState(false);

  const handleViewDetails = (employee: any) => {
    setSelectedEmployee(employee);
    setIsDetailsModalOpen(true);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  const filteredEmployees = employeeCompliance.filter((employee: any) => {
    const matchesSearch = employee.employeeName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         employee.employeeId.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = !filterDepartment || filterDepartment === "all" || employee.department === filterDepartment;
    const matchesStatus = !filterStatus || filterStatus === "all" || employee.complianceStatus === filterStatus;
    return matchesSearch && matchesDepartment && matchesStatus;
  });

  const departmentSet = new Set(employeeCompliance.map((emp: any) => emp.department));
  const uniqueDepartments = Array.from(departmentSet);
  const statusSet = new Set(employeeCompliance.map((emp: any) => emp.complianceStatus));
  const uniqueStatuses = Array.from(statusSet);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Compliant':
        return 'bg-green-100 text-compliance-green hover:bg-green-100';
      case 'Expiring Soon':
        return 'bg-orange-100 text-alert-orange hover:bg-orange-100';
      case 'Overdue':
        return 'bg-red-100 text-critical-red hover:bg-red-100';
      default:
        return 'bg-gray-100 text-gray-600 hover:bg-gray-100';
    }
  };

  const canViewAllRecords = user?.role === 'hr_admin' || user?.role === 'manager';

  return (
    <div className="min-h-screen flex bg-gray-50">
      <Sidebar />

      <main className="flex-1 ml-64">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-20">
          <div className="px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-gray-900" data-testid="text-records-title">Employee Training Records</h2>
                <p className="text-gray-600 mt-1">Monitor individual training compliance and certification status</p>
              </div>

              <div className="flex items-center space-x-4">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                  <Input 
                    type="text" 
                    placeholder="Search employees..." 
                    className="pl-10 pr-4 w-80"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    data-testid="input-search-employees"
                  />
                </div>

                <Select value={filterDepartment} onValueChange={setFilterDepartment}>
                  <SelectTrigger className="w-48" data-testid="select-department-filter">
                    <Filter className="h-4 w-4 mr-2" />
                    <SelectValue placeholder="All Departments" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Departments</SelectItem>
                    {uniqueDepartments.map((dept: string) => (
                      <SelectItem key={dept} value={dept}>{dept}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <Select value={filterStatus} onValueChange={setFilterStatus}>
                  <SelectTrigger className="w-48" data-testid="select-status-filter">
                    <SelectValue placeholder="All Status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    {uniqueStatuses.map((status: string) => (
                      <SelectItem key={status} value={status}>{status}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <Button 
                  variant="outline" 
                  className="text-manufacturing-blue border-manufacturing-blue hover:bg-blue-50"
                  data-testid="button-export-records"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Export
                </Button>
              </div>
            </div>
          </div>
        </header>

        <div className="p-6 space-y-6">
          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-600 text-sm font-medium">Total Employees</p>
                    <p className="text-3xl font-bold text-gray-900 mt-2" data-testid="text-total-employees">
                      {employeeCompliance.length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                    <Users className="h-6 w-6 text-manufacturing-blue" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-600 text-sm font-medium">Compliant</p>
                    <p className="text-3xl font-bold text-compliance-green mt-2" data-testid="text-compliant-count">
                      {employeeCompliance.filter((emp: any) => emp.complianceStatus === 'Compliant').length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                    <Award className="h-6 w-6 text-compliance-green" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-600 text-sm font-medium">Expiring Soon</p>
                    <p className="text-3xl font-bold text-alert-orange mt-2" data-testid="text-expiring-count">
                      {employeeCompliance.filter((emp: any) => emp.complianceStatus === 'Expiring Soon').length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
                    <Calendar className="h-6 w-6 text-alert-orange" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-600 text-sm font-medium">Overdue</p>
                    <p className="text-3xl font-bold text-critical-red mt-2" data-testid="text-overdue-count">
                      {employeeCompliance.filter((emp: any) => emp.complianceStatus === 'Overdue').length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-red-100 rounded-lg flex items-center justify-center">
                    <FileText className="h-6 w-6 text-critical-red" />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Employee Records Table */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg font-semibold">Employee Training Records</CardTitle>
            </CardHeader>
            <CardContent>
              {isLoadingCompliance ? (
                <div className="flex items-center justify-center py-12">
                  <div className="animate-pulse text-gray-500">Loading employee records...</div>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead className="bg-gray-50 border-b border-gray-200">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Employee</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Department</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Compliance Status</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Last Training</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Next Due</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {filteredEmployees.length === 0 ? (
                        <tr>
                          <td colSpan={6} className="px-6 py-8 text-center text-gray-500" data-testid="text-no-employees">
                            {searchTerm || filterDepartment || filterStatus ? "No employees match your filters" : "No employee records available"}
                          </td>
                        </tr>
                      ) : (
                        filteredEmployees.map((employee: any, index: number) => (
                          <tr key={employee.employeeId} className="hover:bg-gray-50" data-testid={`employee-record-${index}`}>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div className="flex items-center">
                                <Avatar className="h-8 w-8">
                                  <AvatarImage src="" alt={employee.employeeName} />
                                  <AvatarFallback className="bg-manufacturing-blue text-white text-sm">
                                    {employee.employeeName.split(' ').map((n: string) => n[0]).join('').slice(0, 2)}
                                  </AvatarFallback>
                                </Avatar>
                                <div className="ml-4">
                                  <div className="text-sm font-medium text-gray-900" data-testid={`text-employee-name-${index}`}>
                                    {employee.employeeName}
                                  </div>
                                  <div className="text-sm text-gray-500" data-testid={`text-employee-id-${index}`}>
                                    {employee.employeeId}
                                  </div>
                                </div>
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900" data-testid={`text-department-${index}`}>
                              {employee.department || 'N/A'}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <Badge 
                                className={getStatusColor(employee.complianceStatus)}
                                data-testid={`badge-status-${index}`}
                              >
                                {employee.complianceStatus}
                              </Badge>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900" data-testid={`text-last-training-${index}`}>
                              {employee.lastTraining || 'No training record'}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm" data-testid={`text-next-due-${index}`}>
                              <span className={
                                employee.complianceStatus === 'Overdue' 
                                  ? 'text-critical-red font-medium' 
                                  : employee.complianceStatus === 'Expiring Soon'
                                  ? 'text-alert-orange font-medium'
                                  : 'text-gray-900'
                              }>
                                {employee.nextDue || 'N/A'}
                              </span>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                              <Button 
                                variant="ghost" 
                                size="sm" 
                                className="text-manufacturing-blue hover:text-blue-700 mr-3"
                                onClick={() => handleViewDetails(employee)}
                                data-testid={`button-view-employee-${index}`}
                              >
                                View Details
                              </Button>
                              {canViewAllRecords && (
                                <Button 
                                  variant="ghost" 
                                  size="sm" 
                                  className="text-gray-600 hover:text-gray-900"
                                  data-testid={`button-edit-employee-${index}`}
                                >
                                  Edit
                                </Button>
                              )}
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Training History (could be expanded to show detailed training history for each employee) */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg font-semibold">Recent Training Activity</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center py-8 text-gray-500" data-testid="text-training-history-placeholder">
                Click "View Details" on any employee to see their training history
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Employee Details Modal */}
        <Dialog open={isDetailsModalOpen} onOpenChange={setIsDetailsModalOpen}>
          <DialogContent className="max-w-4xl max-h-screen overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center space-x-2">
                <span>Employee Training Details</span>
                {selectedEmployee && (
                  <span className="text-sm font-normal text-gray-500">
                    - {selectedEmployee.employeeName}
                  </span>
                )}
              </DialogTitle>
            </DialogHeader>

            {selectedEmployee && (
              <div className="space-y-6">
                {/* Employee Info */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-sm font-medium text-gray-600">Employee Information</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-2">
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Name:</span>
                        <span className="text-sm font-medium">{selectedEmployee.employeeName}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Employee ID:</span>
                        <span className="text-sm font-medium">{selectedEmployee.employeeId}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Department:</span>
                        <span className="text-sm font-medium">{selectedEmployee.department || 'N/A'}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Status:</span>
                        <Badge className={getStatusColor(selectedEmployee.complianceStatus)}>
                          {selectedEmployee.complianceStatus}
                        </Badge>
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader>
                      <CardTitle className="text-sm font-medium text-gray-600">Training Summary</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-2">
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Last Training:</span>
                        <span className="text-sm font-medium">{selectedEmployee.lastTraining || 'No training record'}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Next Due:</span>
                        <span className="text-sm font-medium">{selectedEmployee.nextDue || 'N/A'}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-500">Total Trainings:</span>
                        <span className="text-sm font-medium">
                          {trainingEnrollments.filter((enrollment: any) => enrollment.employeeId === selectedEmployee.employeeId).length}
                        </span>
                      </div>
                    </CardContent>
                  </Card>
                </div>

                {/* Training History */}
                <Card>
                  <CardHeader>
                    <CardTitle className="text-sm font-medium text-gray-600">Training History</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {trainingEnrollments
                        .filter((enrollment: any) => enrollment.employeeId === selectedEmployee.employeeId)
                        .length === 0 ? (
                        <div className="text-center py-8 text-gray-500">
                          No training records found for this employee
                        </div>
                      ) : (
                        trainingEnrollments
                          .filter((enrollment: any) => enrollment.employeeId === selectedEmployee.employeeId)
                          .map((enrollment: any, index: number) => (
                            <div key={index} className="flex items-center justify-between p-3 border border-gray-200 rounded-lg">
                              <div className="flex items-center space-x-3">
                                <div className="w-8 h-8 bg-manufacturing-blue rounded-full flex items-center justify-center">
                                  <BookOpen className="h-4 w-4 text-white" />
                                </div>
                                <div>
                                  <h4 className="text-sm font-medium text-gray-900">
                                    Training Session {enrollment.sessionId || 'N/A'}
                                  </h4>
                                  <p className="text-xs text-gray-500">
                                    Status: {enrollment.status} 
                                    {enrollment.completionDate && ` â€¢ Completed: ${new Date(enrollment.completionDate).toLocaleDateString()}`}
                                  </p>
                                </div>
                              </div>
                              <div className="text-right">
                                {enrollment.score && (
                                  <span className="text-sm font-medium text-gray-900">
                                    Score: {enrollment.score}%
                                  </span>
                                )}
                                <Badge 
                                  className={
                                    enrollment.status === 'completed' 
                                      ? 'bg-compliance-green text-white ml-2' 
                                      : enrollment.status === 'enrolled'
                                      ? 'bg-manufacturing-blue text-white ml-2'
                                      : 'bg-gray-500 text-white ml-2'
                                  }
                                >
                                  {enrollment.status}
                                </Badge>
                              </div>
                            </div>
                          ))
                      )}
                    </div>
                  </CardContent>
                </Card>

                <div className="flex justify-end">
                  <Button 
                    variant="outline" 
                    onClick={() => setIsDetailsModalOpen(false)}
                    data-testid="button-close-details"
                  >
                    Close
                  </Button>
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </main>
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Building, Lock, User } from "lucide-react";

export default function Login() {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [credentials, setCredentials] = useState({
    username: "",
    password: ""
  });

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) {
        throw new Error("Invalid credentials");
      }

      const data = await response.json();

      // Store token in localStorage
      localStorage.setItem('authToken', data.token);

      toast({
        title: "Success",
        description: "Logged in successfully",
      });

      // Redirect to dashboard
      window.location.href = "/";
    } catch (error) {
      toast({
        title: "Error",
        description: "Invalid username or password",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <div className="mx-auto h-12 w-12 bg-manufacturing-blue rounded-lg flex items-center justify-center">
            <Building className="h-6 w-6 text-white" />
          </div>
          <h2 className="mt-6 text-3xl font-bold text-gray-900">
            Training Management System
          </h2>
          <p className="mt-2 text-sm text-gray-600">
            Sign in to manage training and compliance
          </p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="text-center">Sign In</CardTitle>
          </CardHeader>
          <CardContent>
            <form className="space-y-6" onSubmit={handleLogin}>
              <div>
                <Label htmlFor="username">Username</Label>
                <div className="mt-1 relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <User className="h-4 w-4 text-gray-400" />
                  </div>
                  <Input
                    id="username"
                    name="username"
                    type="text"
                    required
                    className="pl-10"
                    placeholder="Enter your username"
                    value={credentials.username}
                    onChange={(e) => setCredentials({ ...credentials, username: e.target.value })}
                    data-testid="input-username"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="password">Password</Label>
                <div className="mt-1 relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Lock className="h-4 w-4 text-gray-400" />
                  </div>
                  <Input
                    id="password"
                    name="password"
                    type="password"
                    required
                    className="pl-10"
                    placeholder="Enter your password"
                    value={credentials.password}
                    onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
                    data-testid="input-password"
                  />
                </div>
              </div>

              <div>
                <Button
                  type="submit"
                  className="w-full bg-manufacturing-blue hover:bg-blue-700"
                  disabled={isLoading}
                  data-testid="button-login"
                >
                  {isLoading ? "Signing in..." : "Sign in"}
                </Button>
              </div>
            </form>

            <div className="mt-6 p-4 bg-gray-50 rounded-lg">
              <h4 className="text-sm font-medium text-gray-900 mb-2">Demo Accounts:</h4>
              <div className="space-y-1 text-xs text-gray-600">
                <div><strong>Admin:</strong> admin / admin123</div>
                <div><strong>Manager:</strong> manager / manager123</div>
                <div><strong>Employee:</strong> employee / employee123</div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

